import{_ as a,c as t,o as n,ae as i}from"./chunks/framework.Dgg8-8ov.js";const m=JSON.parse('{"title":"Embed Builder System","description":"","frontmatter":{},"headers":[],"relativePath":"features/content-creation/EMBED_BUILDER.md","filePath":"features/content-creation/EMBED_BUILDER.md"}'),s={name:"features/content-creation/EMBED_BUILDER.md"};function o(l,e,r,d,c,u){return n(),t("div",null,e[0]||(e[0]=[i(`<h1 id="embed-builder-system" tabindex="-1">Embed Builder System <a class="header-anchor" href="#embed-builder-system" aria-label="Permalink to &quot;Embed Builder System&quot;">​</a></h1><p>Create beautiful, professional Discord embeds with an easy-to-use interactive interface.</p><h2 id="🎨-overview" tabindex="-1">🎨 Overview <a class="header-anchor" href="#🎨-overview" aria-label="Permalink to &quot;🎨 Overview&quot;">​</a></h2><p>The Embed Builder allows server administrators to create rich, visually appealing embeds without any coding knowledge. Perfect for announcements, rules, welcome messages, and more.</p><h2 id="✨-features" tabindex="-1">✨ Features <a class="header-anchor" href="#✨-features" aria-label="Permalink to &quot;✨ Features&quot;">​</a></h2><h3 id="interactive-builder-interface" tabindex="-1">Interactive Builder Interface <a class="header-anchor" href="#interactive-builder-interface" aria-label="Permalink to &quot;Interactive Builder Interface&quot;">​</a></h3><ul><li><strong>Live Preview</strong>: See your embed as you build it</li><li><strong>Button-Based Controls</strong>: Easy point-and-click interface</li><li><strong>Real-Time Updates</strong>: Changes appear immediately</li><li><strong>Template System</strong>: Save and reuse embed designs</li></ul><h3 id="embed-components" tabindex="-1">Embed Components <a class="header-anchor" href="#embed-components" aria-label="Permalink to &quot;Embed Components&quot;">​</a></h3><ul><li><strong>Title &amp; Description</strong>: Main content areas</li><li><strong>Author Section</strong>: Name, icon, and clickable link</li><li><strong>Footer</strong>: Bottom text with optional icon</li><li><strong>Thumbnail &amp; Images</strong>: Visual elements</li><li><strong>Fields</strong>: Up to 25 organized data sections</li><li><strong>Colors</strong>: Custom color themes</li><li><strong>Timestamps</strong>: Automatic or custom time display</li><li><strong>URLs</strong>: Clickable embed titles</li></ul><h3 id="advanced-features" tabindex="-1">Advanced Features <a class="header-anchor" href="#advanced-features" aria-label="Permalink to &quot;Advanced Features&quot;">​</a></h3><ul><li><strong>Field Management</strong>: Add, edit, remove, and reorder fields</li><li><strong>Inline Fields</strong>: Side-by-side field layout</li><li><strong>Color Picker</strong>: Hex color codes or presets</li><li><strong>URL Validation</strong>: Ensures links work properly</li><li><strong>Character Limits</strong>: Automatic validation and warnings</li><li><strong>Template Saving</strong>: Store designs for future use</li></ul><hr><h2 id="🚀-quick-start" tabindex="-1">🚀 Quick Start <a class="header-anchor" href="#🚀-quick-start" aria-label="Permalink to &quot;🚀 Quick Start&quot;">​</a></h2><h3 id="_1-launch-the-builder" tabindex="-1">1. Launch the Builder <a class="header-anchor" href="#_1-launch-the-builder" aria-label="Permalink to &quot;1. Launch the Builder&quot;">​</a></h3><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">/embed</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> builder</span></span></code></pre></div><h3 id="_2-set-basic-information" tabindex="-1">2. Set Basic Information <a class="header-anchor" href="#_2-set-basic-information" aria-label="Permalink to &quot;2. Set Basic Information&quot;">​</a></h3><ul><li>Click <strong>&quot;Set Title&quot;</strong> to add a main heading</li><li>Click <strong>&quot;Set Description&quot;</strong> for the main content</li><li>Choose a <strong>color theme</strong> with the color picker</li></ul><h3 id="_3-add-visual-elements" tabindex="-1">3. Add Visual Elements <a class="header-anchor" href="#_3-add-visual-elements" aria-label="Permalink to &quot;3. Add Visual Elements&quot;">​</a></h3><ul><li><strong>Author</strong>: Add name/icon at the top</li><li><strong>Thumbnail</strong>: Small image on the right</li><li><strong>Image</strong>: Large image in the embed</li><li><strong>Footer</strong>: Bottom text with optional icon</li></ul><h3 id="_4-add-fields-optional" tabindex="-1">4. Add Fields (Optional) <a class="header-anchor" href="#_4-add-fields-optional" aria-label="Permalink to &quot;4. Add Fields (Optional)&quot;">​</a></h3><ul><li>Click <strong>&quot;Add Field&quot;</strong> for organized information</li><li>Set <strong>field names</strong> and <strong>values</strong></li><li>Choose <strong>inline</strong> for side-by-side layout</li></ul><h3 id="_5-send-your-embed" tabindex="-1">5. Send Your Embed <a class="header-anchor" href="#_5-send-your-embed" aria-label="Permalink to &quot;5. Send Your Embed&quot;">​</a></h3><ul><li>Click <strong>&quot;Send to Channel&quot;</strong> when finished</li><li>Choose the destination channel</li><li>Add optional message content</li></ul><hr><h2 id="📋-commands" tabindex="-1">📋 Commands <a class="header-anchor" href="#📋-commands" aria-label="Permalink to &quot;📋 Commands&quot;">​</a></h2><h3 id="main-command" tabindex="-1">Main Command <a class="header-anchor" href="#main-command" aria-label="Permalink to &quot;Main Command&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Command</th><th>Description</th></tr></thead><tbody><tr><td><code>/embed builder</code></td><td>Launch the interactive embed builder interface</td></tr></tbody></table><h3 id="builder-interface-options" tabindex="-1">Builder Interface Options <a class="header-anchor" href="#builder-interface-options" aria-label="Permalink to &quot;Builder Interface Options&quot;">​</a></h3><ul><li><strong>🎨 Set Title</strong> - Add main embed heading</li><li><strong>📝 Set Description</strong> - Add main embed content</li><li><strong>🎯 Set Author</strong> - Add author name, icon, and link</li><li><strong>🖼️ Set Thumbnail</strong> - Add small image (right side)</li><li><strong>🌅 Set Image</strong> - Add large image (bottom)</li><li><strong>👣 Set Footer</strong> - Add bottom text and icon</li><li><strong>🌈 Set Color</strong> - Choose embed color theme</li><li><strong>⏰ Toggle Timestamp</strong> - Add current time display</li><li><strong>🔗 Set URL</strong> - Make title clickable</li><li><strong>➕ Add Field</strong> - Add organized data sections</li><li><strong>✏️ Edit Fields</strong> - Modify existing fields</li><li><strong>🗑️ Remove Field</strong> - Delete fields</li><li><strong>📋 Load Template</strong> - Use saved designs</li><li><strong>💾 Save Template</strong> - Store current design</li><li><strong>📤 Send to Channel</strong> - Publish your embed</li><li><strong>❌ Cancel</strong> - Exit without saving</li></ul><hr><h2 id="🔧-configuration-guide" tabindex="-1">🔧 Configuration Guide <a class="header-anchor" href="#🔧-configuration-guide" aria-label="Permalink to &quot;🔧 Configuration Guide&quot;">​</a></h2><h3 id="basic-embed-setup" tabindex="-1">Basic Embed Setup <a class="header-anchor" href="#basic-embed-setup" aria-label="Permalink to &quot;Basic Embed Setup&quot;">​</a></h3><ol><li><p><strong>Title</strong>: Main heading (up to 256 characters)</p><ul><li>Should be descriptive and eye-catching</li><li>Example: &quot;Server Rules&quot; or &quot;Weekly Event&quot;</li></ul></li><li><p><strong>Description</strong>: Main content (up to 4096 characters)</p><ul><li>Supports Discord markdown formatting</li><li>Use <code>**bold**</code>, <code>*italic*</code>, <code>__underline__</code></li><li>Add <code>\\n</code> for line breaks</li></ul></li><li><p><strong>Color</strong>: Visual theme</p><ul><li>Use hex codes: <code>#FF0000</code> for red</li><li>Or color names: <code>red</code>, <code>blue</code>, <code>green</code></li><li>Default: Discord blurple (<code>#5865F2</code>)</li></ul></li></ol><h3 id="author-section" tabindex="-1">Author Section <a class="header-anchor" href="#author-section" aria-label="Permalink to &quot;Author Section&quot;">​</a></h3><ul><li><strong>Name</strong>: Person or organization name</li><li><strong>Icon</strong>: Small image next to name (URL required)</li><li><strong>URL</strong>: Make author name clickable</li></ul><h3 id="images" tabindex="-1">Images <a class="header-anchor" href="#images" aria-label="Permalink to &quot;Images&quot;">​</a></h3><ul><li><strong>Thumbnail</strong>: Small image on the right (recommended: 128x128px)</li><li><strong>Main Image</strong>: Large image at bottom (recommended: 400x200px)</li><li><strong>Must be valid image URLs</strong> (png, jpg, gif, webp)</li></ul><h3 id="fields-system" tabindex="-1">Fields System <a class="header-anchor" href="#fields-system" aria-label="Permalink to &quot;Fields System&quot;">​</a></h3><p>Perfect for organizing information:</p><ul><li><strong>Name</strong>: Field heading (up to 256 characters)</li><li><strong>Value</strong>: Field content (up to 1024 characters)</li><li><strong>Inline</strong>: Display side-by-side (up to 3 per row)</li><li><strong>Maximum</strong>: 25 fields per embed</li></ul><h3 id="footer" tabindex="-1">Footer <a class="header-anchor" href="#footer" aria-label="Permalink to &quot;Footer&quot;">​</a></h3><ul><li><strong>Text</strong>: Bottom message (up to 2048 characters)</li><li><strong>Icon</strong>: Small image next to footer text</li><li><strong>Timestamp</strong>: Automatically adds current time</li></ul><hr><h2 id="🎯-use-cases-examples" tabindex="-1">🎯 Use Cases &amp; Examples <a class="header-anchor" href="#🎯-use-cases-examples" aria-label="Permalink to &quot;🎯 Use Cases &amp; Examples&quot;">​</a></h2><h3 id="server-announcements" tabindex="-1">Server Announcements <a class="header-anchor" href="#server-announcements" aria-label="Permalink to &quot;Server Announcements&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Title: 📢 Server Update v2.1</span></span>
<span class="line"><span>Description: We&#39;ve added new features and improved performance!</span></span>
<span class="line"><span>Color: #00FF00 (green)</span></span>
<span class="line"><span>Fields:</span></span>
<span class="line"><span>  - Name: &quot;New Features&quot;, Value: &quot;• New music commands\\n• AI chatbot\\n• Auto-moderation&quot;</span></span>
<span class="line"><span>  - Name: &quot;Bug Fixes&quot;, Value: &quot;• Fixed connection issues\\n• Improved stability&quot;</span></span>
<span class="line"><span>Footer: &quot;Update deployed on&quot;</span></span>
<span class="line"><span>Timestamp: Enabled</span></span></code></pre></div><h3 id="welcome-messages" tabindex="-1">Welcome Messages <a class="header-anchor" href="#welcome-messages" aria-label="Permalink to &quot;Welcome Messages&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Title: Welcome to Our Community! 🎉</span></span>
<span class="line"><span>Description: We&#39;re excited to have you here!</span></span>
<span class="line"><span>Color: #FF6B6B (warm red)</span></span>
<span class="line"><span>Author: Server name with server icon</span></span>
<span class="line"><span>Thumbnail: Server logo</span></span>
<span class="line"><span>Fields:</span></span>
<span class="line"><span>  - Name: &quot;📋 Read the Rules&quot;, Value: &quot;Check #rules channel&quot;</span></span>
<span class="line"><span>  - Name: &quot;🎮 Get Started&quot;, Value: &quot;Visit #getting-started&quot;</span></span>
<span class="line"><span>  - Name: &quot;❓ Need Help?&quot;, Value: &quot;Ask in #support&quot;</span></span></code></pre></div><h3 id="event-announcements" tabindex="-1">Event Announcements <a class="header-anchor" href="#event-announcements" aria-label="Permalink to &quot;Event Announcements&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Title: 🎮 Gaming Tournament</span></span>
<span class="line"><span>Description: Join us for our monthly gaming competition!</span></span>
<span class="line"><span>Color: #9B59B6 (purple)</span></span>
<span class="line"><span>Image: Tournament banner image</span></span>
<span class="line"><span>Fields:</span></span>
<span class="line"><span>  - Name: &quot;📅 Date&quot;, Value: &quot;Saturday, June 25th&quot;, Inline: true</span></span>
<span class="line"><span>  - Name: &quot;⏰ Time&quot;, Value: &quot;7:00 PM EST&quot;, Inline: true</span></span>
<span class="line"><span>  - Name: &quot;🏆 Prize&quot;, Value: &quot;$100 Steam Gift Card&quot;, Inline: true  </span></span>
<span class="line"><span>  - Name: &quot;🎯 How to Join&quot;, Value: &quot;React with 🎮 to participate&quot;</span></span>
<span class="line"><span>Footer: &quot;Good luck to all participants!&quot;</span></span></code></pre></div><h3 id="information-panels" tabindex="-1">Information Panels <a class="header-anchor" href="#information-panels" aria-label="Permalink to &quot;Information Panels&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Title: 🤖 Bot Commands</span></span>
<span class="line"><span>Description: Here are the most useful bot commands:</span></span>
<span class="line"><span>Color: #3498DB (blue)</span></span>
<span class="line"><span>Fields:</span></span>
<span class="line"><span>  - Name: &quot;🎵 Music&quot;, Value: &quot;\`/play\` - Play music\\n\`/queue\` - Show queue&quot;, Inline: true</span></span>
<span class="line"><span>  - Name: &quot;🛡️ Moderation&quot;, Value: &quot;\`/cleanup\` - Delete messages\\n\`/modlog\` - Setup logging&quot;, Inline: true</span></span>
<span class="line"><span>  - Name: &quot;⚙️ Settings&quot;, Value: &quot;\`/settings\` - Configure bot\\n\`/help\` - Get help&quot;, Inline: true</span></span></code></pre></div><hr><h2 id="💡-best-practices" tabindex="-1">💡 Best Practices <a class="header-anchor" href="#💡-best-practices" aria-label="Permalink to &quot;💡 Best Practices&quot;">​</a></h2><h3 id="design-tips" tabindex="-1">Design Tips <a class="header-anchor" href="#design-tips" aria-label="Permalink to &quot;Design Tips&quot;">​</a></h3><ul><li><strong>Keep it clean</strong>: Don&#39;t overload with too much information</li><li><strong>Use consistent colors</strong>: Match your server&#39;s theme</li><li><strong>Organize with fields</strong>: Break up large text blocks</li><li><strong>Test on mobile</strong>: Ensure it looks good on all devices</li></ul><h3 id="content-guidelines" tabindex="-1">Content Guidelines <a class="header-anchor" href="#content-guidelines" aria-label="Permalink to &quot;Content Guidelines&quot;">​</a></h3><ul><li><strong>Clear titles</strong>: Make purpose obvious at a glance</li><li><strong>Concise descriptions</strong>: Get to the point quickly</li><li><strong>Use formatting</strong>: Bold important information</li><li><strong>Add emojis</strong>: Make it visually appealing (but don&#39;t overdo it)</li></ul><h3 id="technical-tips" tabindex="-1">Technical Tips <a class="header-anchor" href="#technical-tips" aria-label="Permalink to &quot;Technical Tips&quot;">​</a></h3><ul><li><strong>Image URLs</strong>: Use reliable hosting (Discord CDN, Imgur, etc.)</li><li><strong>Field limits</strong>: Stay under character limits for each section</li><li><strong>URL validation</strong>: Test all links before publishing</li><li><strong>Mobile preview</strong>: Check how it appears on different screen sizes</li></ul><h3 id="accessibility" tabindex="-1">Accessibility <a class="header-anchor" href="#accessibility" aria-label="Permalink to &quot;Accessibility&quot;">​</a></h3><ul><li><strong>Alt text</strong>: Describe images in surrounding text</li><li><strong>Color contrast</strong>: Ensure text is readable</li><li><strong>Clear language</strong>: Avoid jargon and complex terms</li><li><strong>Logical order</strong>: Present information in a sensible flow</li></ul><hr><h2 id="🔒-permissions-required" tabindex="-1">🔒 Permissions Required <a class="header-anchor" href="#🔒-permissions-required" aria-label="Permalink to &quot;🔒 Permissions Required&quot;">​</a></h2><h3 id="to-use-embed-builder" tabindex="-1">To Use Embed Builder <a class="header-anchor" href="#to-use-embed-builder" aria-label="Permalink to &quot;To Use Embed Builder&quot;">​</a></h3><ul><li><strong>Manage Messages</strong> permission</li><li>OR <strong>Administrator</strong> permission</li><li>OR Be the server owner</li></ul><h3 id="to-send-embeds" tabindex="-1">To Send Embeds <a class="header-anchor" href="#to-send-embeds" aria-label="Permalink to &quot;To Send Embeds&quot;">​</a></h3><ul><li>Bot needs <strong>Send Messages</strong> permission in target channel</li><li>Bot needs <strong>Embed Links</strong> permission</li><li>User needs permission to send messages in target channel</li></ul><hr><h2 id="🆘-troubleshooting" tabindex="-1">🆘 Troubleshooting <a class="header-anchor" href="#🆘-troubleshooting" aria-label="Permalink to &quot;🆘 Troubleshooting&quot;">​</a></h2><h3 id="builder-won-t-open" tabindex="-1"><strong>Builder Won&#39;t Open</strong> <a class="header-anchor" href="#builder-won-t-open" aria-label="Permalink to &quot;**Builder Won&#39;t Open**&quot;">​</a></h3><ul><li>Check you have <strong>Manage Messages</strong> permission</li><li>Ensure bot is online and responsive</li><li>Try in a different channel</li></ul><h3 id="images-not-showing" tabindex="-1"><strong>Images Not Showing</strong> <a class="header-anchor" href="#images-not-showing" aria-label="Permalink to &quot;**Images Not Showing**&quot;">​</a></h3><ul><li>Verify image URLs are direct links to image files</li><li>Check image hosting service is reliable</li><li>Ensure URLs use <code>https://</code> not <code>http://</code></li><li>Test URLs in a web browser first</li></ul><h3 id="embed-too-long" tabindex="-1"><strong>Embed Too Long</strong> <a class="header-anchor" href="#embed-too-long" aria-label="Permalink to &quot;**Embed Too Long**&quot;">​</a></h3><ul><li>Description limit: 4,096 characters</li><li>Title limit: 256 characters</li><li>Field value limit: 1,024 characters each</li><li>Total embed limit: 6,000 characters</li></ul><h3 id="can-t-send-to-channel" tabindex="-1"><strong>Can&#39;t Send to Channel</strong> <a class="header-anchor" href="#can-t-send-to-channel" aria-label="Permalink to &quot;**Can&#39;t Send to Channel**&quot;">​</a></h3><ul><li>Check bot has permissions in target channel</li><li>Verify channel still exists</li><li>Ensure bot has <strong>Embed Links</strong> permission</li></ul><h3 id="templates-not-saving" tabindex="-1"><strong>Templates Not Saving</strong> <a class="header-anchor" href="#templates-not-saving" aria-label="Permalink to &quot;**Templates Not Saving**&quot;">​</a></h3><ul><li>Check database connection</li><li>Verify you have proper permissions</li><li>Try creating template with shorter name</li></ul><hr><h2 id="🎨-advanced-features" tabindex="-1">🎨 Advanced Features <a class="header-anchor" href="#🎨-advanced-features" aria-label="Permalink to &quot;🎨 Advanced Features&quot;">​</a></h2><h3 id="template-system" tabindex="-1">Template System <a class="header-anchor" href="#template-system" aria-label="Permalink to &quot;Template System&quot;">​</a></h3><ul><li><strong>Save frequently used designs</strong></li><li><strong>Share templates</strong> between team members</li><li><strong>Quick loading</strong> for repeated embed types</li><li><strong>Backup important</strong> embed configurations</li></ul><h3 id="color-schemes" tabindex="-1">Color Schemes <a class="header-anchor" href="#color-schemes" aria-label="Permalink to &quot;Color Schemes&quot;">​</a></h3><p>Popular embed color codes:</p><ul><li><strong>Success</strong>: <code>#00FF00</code> or <code>#2ECC71</code></li><li><strong>Warning</strong>: <code>#FFFF00</code> or <code>#F39C12</code></li><li><strong>Error</strong>: <code>#FF0000</code> or <code>#E74C3C</code></li><li><strong>Info</strong>: <code>#00FFFF</code> or <code>#3498DB</code></li><li><strong>Discord</strong>: <code>#5865F2</code></li></ul><h3 id="integration-tips" tabindex="-1">Integration Tips <a class="header-anchor" href="#integration-tips" aria-label="Permalink to &quot;Integration Tips&quot;">​</a></h3><ul><li><strong>Use with welcome system</strong> for greeting new members</li><li><strong>Combine with modlog</strong> for formatted notifications</li><li><strong>Pair with ticket system</strong> for support responses</li><li><strong>Integrate with announcements</strong> for server updates</li></ul><hr><p><em>The Embed Builder makes professional Discord embeds accessible to everyone - no coding required!</em></p>`,91)]))}const g=a(s,[["render",o]]);export{m as __pageData,g as default};
